public void AddToCartClick(){
    int size = driver.findElements(By.xpath("/hierarchy/android.widget.FrameLayout/android.widget.LinearLayout/android.widget.FrameLayout/android.widget.LinearLayout/android.widget.FrameLayout/android.widget.FrameLayout/android.view.ViewGroup/android.view.ViewGroup/android.view.ViewGroup/android.view.ViewGroup/android.view.ViewGroup/android.view.ViewGroup/android.view.ViewGroup/android.view.ViewGroup/android.view.ViewGroup/android.view.ViewGroup[2]/android.view.ViewGroup/android.view.ViewGroup/android.view.ViewGroup/android.view.ViewGroup/android.view.ViewGroup[2]/android.view.ViewGroup[3]")).size();
    if(size > 0){
        click(AddToCartSmall);
    }
    else{
        click(AddToCart);
    }
}
public void BuyConfirmClick(){
    click(BuyConfirm);
}
public void CartClick(){
    click(Cart);
}
public void BuyNow(){
    click(BuyProduct);
}
public void LikeProduct(){
    click(LikeProduct);
}
public void ShareProduct(){
    click(ShareProduct);
}
public String getProductName(){
    return getText(ProductName);
}
public void clickShopPage(){
    click(ShopPage);
}
public String getShopName(){
    return getText(ShopName);
}
